INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/'弥诺陶洛斯', /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】获得强大力量的金牛座魔星兽。\n其战斧的攻击超越了力量的极限，能将一切粉碎。\n\n·连结爆发时，解除敌方全体的屏障，\n　并根据弥诺陶洛斯的物理攻击力\n　造成一定比例的物理伤害。\n　同时使其陷入烧伤状态，中幅度降低其技能值。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对魔法攻击力最高的一名敌人造成物理伤害\n　（大），将其击退的同时使其眩晕。\n　　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方三名角色造成魔法伤害（中），\n并使其麻痹。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300101, /*unit_name*/'弥诺陶洛斯', /*prefab_id*/300101, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】获得强大力量的金牛座魔星兽。\n其战斧的攻击超越了力量的极限，能将一切粉碎。\n\n·连结爆发时，解除敌方全体的屏障，\n　并根据弥诺陶洛斯的物理攻击力\n　造成一定比例的物理伤害。\n　同时使其陷入烧伤状态，中幅度降低其技能值。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对魔法攻击力最高的一名敌人造成物理伤害（大），\n　将其击退的同时\n　使其眩晕。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方三名角色造成魔法伤害（中），\n并使其麻痹。\n·中幅度提升自身物理攻击力，大幅度提升\n　魔法攻击力，对前方第一、第二名角色\n　造成物理伤害（大）。\n　技能发动后，小幅度降低自身的物理防御力，\n　中幅度降低魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300102, /*unit_name*/'弥诺陶洛斯', /*prefab_id*/300102, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】获得强大力量的金牛座魔星兽。\n其战斧的攻击超越了力量的极限，能将一切粉碎。\n\n·连结爆发时，解除敌方全体的屏障，\n　并根据弥诺陶洛斯的物理攻击力\n　造成一定比例的物理伤害。\n　同时使其陷入烧伤状态，中幅度降低其技能值。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对魔法攻击力最高的一名敌人造成物理伤害\n　（大），将其击退的同时使其眩晕。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方三名角色造成魔法伤害（中），\n并使其麻痹。\n·中幅度提升自身物理攻击力，大幅度提升\n　魔法攻击力，对前方第一、第二名角色\n　造成物理伤害（大）。\n　技能发动后，小幅度降低自身的物理防御力，\n　中幅度降低魔法防御力。\n·展开战斗领域。\n　【战斗领域效果】\n领域整体被火焰包围，并发动\n以下效果。\n·无视挑衅，并将物理攻击力或魔法攻击力\n　最高的一名我方角色的物理攻击力和魔法攻击力\n　降至0。\n·特大幅度降低弥诺陶洛斯的物理防御力和\n　魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300200, /*unit_name*/'利蝎巴鲁托', /*prefab_id*/300200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'使用猛毒的尾棘散播灾厄的天蝎座魔星兽。\n守护沙漠的神虫失去信仰后，变成了邪神。\n\n·对敌方全体造成大量物理伤害。\n　BOSS每次使用必杀技能，\n　都会提升对敌人造成的伤害。\n　这个效果最多可以累积三次。\n·对前方范围内的敌人造成中等物理伤害，\n　并赋予中毒状态。\n·解除自身的属性值降低效果，\n　并提升自己的物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300201, /*unit_name*/'利蝎巴鲁托', /*prefab_id*/300201, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·连结爆发时，对敌方全体造成\n　物理伤害（中）。使用此技能后，每次攻击\n　敌人，利蝎巴鲁托对该名敌人\n　造成的伤害都会提高。\n　这个效果最多可以累积3次。\n·以前方一名角色为中心，对小范围内的敌人\n　造成物理伤害（中），并使其陷入毒状态。\n　该技能所造成的毒伤害\n　无法回复技能值。\n·解除自身的属性值降低效果，\n　并中幅度提升自身的物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300202, /*unit_name*/'利蝎巴鲁托', /*prefab_id*/300202, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】被遗留在失去信仰了的神殿里的天蝎座魔星兽。\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\n\n·连结爆发时，对敌方全体造成\n　物理伤害（中）。使用此技能后，每次攻击\n　敌人，利蝎巴鲁托对该名敌人\n　造成的伤害都会提高。\n　这个效果最多可以累积3次。\n·以前方一名角色为中心，对小范围内的敌人\n　造成物理伤害（中），并使其陷入毒状态。\n　该技能所造成的毒伤害\n　无法回复技能值。\n·解除自身的属性值降低效果，\n　并中幅度提升自身的物理防御力。\n·召唤1只利蝎守护者。\n　在召唤时，所有已召唤的利蝎守护者\n　都将被赋予1秒的无敌状态。\n·召唤第3只利蝎守护者时，将根据角色的\n　最大生命值，对敌方全体造成一定比例的魔法伤害，\n　并使其眩晕。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·当利蝎守护者的生命值低于一定值时，\n　对利蝎巴鲁托造成魔法固定伤害，\n　并中幅度降低其物理攻击力，将其消灭。');
