INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/'长老角鹿', /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】一旦爆发便无法对付，其嘶吼声据说能传到天际，传说中的狂野角鹿。\n\n·对敌方全体造成物理伤害（中），并小幅度击飞。\n\n·对前方三名角色造成物理伤害（中），并降低敌人的物理攻击力和物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/'灵魂角鹿', /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】受到异常力量影响而变异的长老角鹿。\n在犄角中注入魔力，释放比打击更强力的一击。\n\n·对前方三名角色造成魔法伤害（大），\n　将其击退的同时使其眩晕。\n·对前方三名角色造成魔法伤害（小），\n并大幅度降低其魔法防御力和行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300802, /*unit_name*/'灵魂角鹿', /*prefab_id*/300802, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】受到异常力量影响而变异的长老角鹿。\n在犄角中注入魔力，释放比打击更强力的一击。\n\n·对前方三名角色造成魔法伤害（大），\n　将其击退的同时使其眩晕。\n　同时为自身展开物理/魔法无效化\n　屏障。\n·对前方三名角色造成魔法伤害（小），\n并大幅度降低其魔法防御力和行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/'暴君寿龟', /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】进化成如岩山般的巨大老龟。\n强力无比的头槌可以毫不费力地撞碎大地。\n\n·连结爆发时，对前方范围造成\n　物理伤害，并使范围内的敌人陷入麻痹状态。\n·降低前方三名角色的物理防御力。');
