INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/'巨型魔像', /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】能召唤出无限的魔像，以岩针同时削减敌人的体力与精神。并能以魔力将无数的魔像残骸重构起来的巨大魔像。\n\n·对前方四名敌人造成物理伤害（中），并降低其技能值。\n\n·召唤魔像。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301201, /*unit_name*/'水晶魔像', /*prefab_id*/301201, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】以月神魔力的结晶包裹全身的巨大魔像。\n附着于全身的结晶既如坚实的盾牌，又如可穿刺一切的锐矛。\n·当水晶魔像获得毒、猛毒、诅咒、烧伤状态时，\n　行动速度将大幅降低。\n·解除自身所有的减益效果，\n　并大幅提升物理防御力与魔法防御力。\n　此外，中幅提升我方除自身以外同伴的\n　物理防御力与魔法防御力。\n·对敌方全体造成物理伤害（中），使其眩晕。\n·对前方范围内的敌人造成物理伤害（大），\n　并小幅提升我方全体的\n　物理攻击力与魔法攻击力。\n　同时中幅回复自身技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301202, /*unit_name*/'寒冰魔像', /*prefab_id*/301202, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】潜伏在冰封大地的洞穴里的\n巨大魔像。附着于全身的结晶\n既如坚实的盾牌，又如可穿刺一切的锐矛。\n\n·寒冰魔像受到毒、猛毒、\n　诅咒、烧伤效果时，\n　行动速度大幅度降低。\n\n·同时解除自身所有的减益效果，\n　并中幅度提升物理防御力和\n　魔法防御力。\n\n·对敌方全体造成\n　物理伤害（中）。\n\n·并对前方范围内的敌人造成\n　物理伤害（大）。');
