INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302900, /*unit_name*/'阿克艾利欧斯', /*prefab_id*/302900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'被寄宿在神瓶中的魔力所吸收的水瓶座魔星兽。\n沉溺于强大的力量，为了支配大海而肆虐。\n\n·必杀技能对敌方全体造成共计6次的\n　魔法伤害（小）。\n　当角色的生命值低于80%时造成击退效果，\n　当低于40%时进一步击退并\n　赋予眩晕状态。\n·对眼前的三名敌方角色造成物理伤害（中）。\n·解除自身的属性值降低效果和敌方全体的\n　属性值提升效果，并提升自身的技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302901, /*unit_name*/'阿克艾利欧斯', /*prefab_id*/302901, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】操纵着寄宿在神瓶中魔力的水瓶座魔星兽。\n喷射的激流可吞没万物，它作为苍海支配者统治着一切。\n\n·连结爆发时，根据角色最大生命值，\n　对敌方全体造成一定比例的魔法伤害。\n　若角色的生命值在80％以下，则将其击退，\n　若在50%以下，则增加击退强度，\n　并赋予眩晕状态。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·解除敌方全体的属性值提升效果，\n　并小幅度提升自身的技能值、物理防御力和魔法防御力。\n·对眼前一名敌人造成物理伤害（特大），\n　对前方第二、第三名角色造成物理伤害（大）。');
