INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305600, /*unit_name*/'异界魔主', /*prefab_id*/305600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】渴望获得月神魔力而出现的异界魔物。\n企图凭魔眼和自己率领的士兵征服露娜之塔。\n\n·对敌方全体造成魔法伤害（大），\n　并中幅度降低其魔法防御力。\n·随机对一名角色造成\n　6次魔法伤害（小）。\n·对后方第三名角色造成魔法伤害（中），\n　并将其长时间拘束。\n\n·同时对敌方全体造成魔法伤害（小）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305700, /*unit_name*/'巨型哥布林', /*prefab_id*/305700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】豪爽地挥舞战斧，身经百战的哥布林战士。\n腋下夹着年幼的战士，为其展示千锤百炼的技艺。\n\n·连结爆发时，中幅度提升自身的\n　物理攻击力，小幅度提升物理防御力，\n　并对眼前的一名敌人造成物理伤害（特大）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305701, /*unit_name*/'巨型哥布林', /*prefab_id*/305701, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'豪爽地挥舞战斧，身经百战的哥布林战士。\n腋下夹着年幼的战士，为其展示千锤百炼的技艺。\n\n·必杀技能中幅提升自身的物理攻击力，\n　小幅提升自身的物理防御力，同时\n　对前方一名敌人造成物理伤害（特大）。');
