INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305800, /*unit_name*/'海蛞蝓', /*prefab_id*/305800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】海神的使者，一种神秘的软体魔物。\n据闻几乎全身都是由带有魔力的海水构成。\n\n·连结爆发时，根据角色的最大生命值，\n　对敌方全体造成一定比例的魔法伤害，并大幅度\n　降低其物理攻击力，中幅度降低魔法攻击力。\n·对前方第二名角色造成魔法伤害（特大），\n　对前方第三名角色造成魔法伤害（中）。\n·在一定范围内，展开大幅降低行动速度和\n　持续造成魔法伤害的领域。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305801, /*unit_name*/'海神使者', /*prefab_id*/305801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】在露娜之塔肆虐的海兽。曾侍奉远古的海神，\n如今却成为了舍弃正义的破坏者。\n\n·对敌方全体造成魔法伤害。\n·在一定范围内，降低敌方的物理攻击力和\n　魔法防御力，并展开造成持续\n　魔法伤害的领域。\n·对前方第二名、第三名敌人\n　造成魔法伤害（大）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305802, /*unit_name*/'海蛞蝓', /*prefab_id*/305802, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】侍奉海神的奇妙魔物。\n当看到有入侵者威胁水域的和平时，\n它便会行使神使之力将其一举消灭。\n\n·对敌方全体造成魔法伤害（大）。\n\n·对前方第二名角色造成魔法伤害（特大），\n　对前方第三名角色造成\n　魔法伤害（中）。\n\n·在一定范围内，展开降低魔法防御力和\n　持续造成魔法伤害的领域。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305900, /*unit_name*/'极彩鸟', /*prefab_id*/305900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.2, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】能散发出惊人的吸引力，有着缤纷色彩的艳丽鸟类。\n若被那份美丽所吸引，便会成为其仆人，袭击同伴。\n\n·必杀技能对魅惑状态以外的全体敌人\n　造成物理伤害（大）。\n·对前方两名角色造成魔法伤害（小），并赋予\n　魅惑状态，再大幅提升物理攻击力\n　与魔法攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305901, /*unit_name*/'极彩鸟', /*prefab_id*/305901, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.2, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】散发着压倒性魅力的妖娆五彩艳鸟。\n一旦被它的美艳所困，便会成为不惜袭击同伴的仆人。\n\n·连结爆发时，除处于诱惑状态的角色以外，\n　对敌方全体造成物理伤害（大）。\n　并同时赋予敌方全体诱惑状态。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n\n·对前方两名角色造成魔法伤害（小），\n　并赋予诱惑状态，随后大幅度提升其物理攻击力和\n　魔法攻击力。');
