INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306400, /*unit_name*/'野性飞鸦', /*prefab_id*/306400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】作为荒山的捕食者君临此地的凶恶怪鸟兽。\n利用通过无数次狩猎锻炼而成的惊人怪力与硬爪袭向入侵者。\n\n·对敌方全体造成物理伤害（大），并降低其技能值上升量。每次发动都会提升造成的伤害。这个效果最多可以累积三次。\n\n·以后方第二名敌人为中心，造成小范围的物理伤害（小），并赋予麻痹状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306500, /*unit_name*/'部落兽人', /*prefab_id*/306500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】边境森林中的兽人们的头目。四处传播其具有独特民族性的奇怪旋律。那奇怪的声音可令敌人陷入混乱。\n\n·对敌方全体造成物理伤害（大），并降低其物理防御力与魔法防御力，同时提升自身的物理攻击力与魔法攻击力。\n\n·对前方四名敌人造成魔法伤害（小），当敌人的生命值降至70%以下时，赋予混乱状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306501, /*unit_name*/'吵闹兽人', /*prefab_id*/306501, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.9, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】被露娜之塔的力量所魅惑的兽人魁首。\n以奇特的太鼓旋律在战场上引起骚乱。\n\n·对敌方全体造成物理伤害（大），并降低其物理防御力\n　和魔法防御力。同时提升自身的\n　物理攻击力和魔法攻击力。\n·对前方三名角色造成魔法伤害（小），\n　并将其小幅度击飞。若敌人生命值低于\n　70%，使其陷入混乱状态。\n·根据剩余的生命值，对敌方全体造成魔法伤害（大），\n并降低其行动速度。\n　同时解除敌方全体的\n　属性值提升效果。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306600, /*unit_name*/'毒液沙罗曼蛇', /*prefab_id*/306600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】在水边洒满剧毒，以此袭击猎物的山椒鱼魔物。积存在其身体里的剧毒十分危险，光是靠近都能令猎物失去意识。\n\n·对敌方全体造成魔法伤害（中），并赋予毒状态。同时赋予猛毒状态。\n\n·对前方范围内的敌人造成魔法伤害（小），并赋予猛毒状态。');
