INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307013, /*unit_name*/'魔石巨人', /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】将【美食殿堂】成员作为暗影召唤。以旅途的回忆为盾，试探三人\n的弱点。\n\n·对前方三名角色造成物理伤害（中）。\n·对敌方全体造成物理伤害（中），\n　并大幅度地将其击退。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307014, /*unit_name*/'魔石巨人', /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】强韧的肉体能抵挡\n一切攻击。以愤怒为食粮，巨人会做出\n怒涛般的强力反击。\n\n·容易受到物理攻击力提升·下降的\n效果影响。\n·连结爆发时，对敌方全体造成物理伤害（中）。\n·对前方三名角色造成物理伤害（大）。\n·保持防御姿势一段时间后，根据时间内所受到的\n　伤害量，对敌方全体造成物理伤害，\n并使前方两名角色麻痹。\n　同时提升自身的物理攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307015, /*unit_name*/'魔石巨人', /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】庞然的巨人解放了\n全身的力量。在强大无比的破坏力前，\n耍小聪明的攻击无法奏效。\n\n·容易受到物理攻击力提升·下降的\n效果影响。\n·连结爆发时，对敌方全体造成物理伤害（中）。\n·对前方三名角色造成物理伤害（大）。\n·保持防御姿势一段时间后，根据时间内所受到的\n　伤害量，对敌方全体造成物理伤害，\n并使前方两名角色麻痹。\n　同时提升自身的物理攻击力。\n·对敌方全体造成物理伤害（大），\n　并大幅度地将其击退。\n·战斗开始后，提升自身的物理攻击力\n　和行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307100, /*unit_name*/'机械莉玛（MODE1）', /*prefab_id*/307100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'神秘巨型兵器的第1形态，结构坚硬并拥有多彩的技能。\n使用苹果型的补给机器填充未知的能量，\n要注意的是，它的连续攻击可以令大范围地区化作焦土！\n\n·连结爆发时，对敌方全体造成物理伤害（大）。\n·对前方一名角色造成物理伤害（小）。\n·在一定范围内，展开降低物理攻击力和回避率，\n　并续赋赋予物理伤害的领域。\n　同时解除前方范围内的角色的\n　属性值提升效果。\n·对前方范围内的敌人造成物理伤害（中），\n　并提升自身的技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307101, /*unit_name*/'机械莉玛（MODE2）', /*prefab_id*/307101, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.2, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'神秘巨型兵器的第2形态，会把所有状态都切换为攻击。\n释放出的原子炉莉玛光线可以将大地燃烧殆尽。\n对无防备的巨大兵器造成会心一击吧！\n\n·连结爆发时，对前方小范围内的敌人\n　造成物理伤害（特大），对中范围内的敌人造成物理伤害（大），\n　并对大范围内的敌人造成物理伤害（中），\n　同时大幅度将其击飞。\n·对前方一名角色造成物理伤害（小）。\n·随机对一名角色造成物理伤害（小）。\n\n·在一定范围内，展开大幅度降低物理攻击力和魔法攻击力\n　并持续赋予魔法伤害的领域。\n　·同时使魔法攻击力最高的两名角色\n　陷入沉默状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307102, /*unit_name*/'机械莉玛（MODE3）', /*prefab_id*/307102, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'神秘巨型兵器的第3形态，会自我修复损耗。\n防卫无人机会阻止外界对满身破绽的本体的攻击。\n使用范围攻击将无人机一一击退，抓住打倒它的好时机！\n\n·大幅度击飞敌方全体，\n　并召唤3台迷你机械莉玛。\n·迷你机械莉玛会对前方一名角色\n　造成物理伤害（小）。\n·迷你机械莉玛会对随机一名角色\n　造成魔法伤害（中）。\n·并根据剩余生命值，对敌方全体\n　造成一定比例的物理伤害（特大）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307200, /*unit_name*/'巴吉里斯克', /*prefab_id*/307200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】追求露娜魔力飞来的大怪鸟。\n率领雏鸟，挥下石化的咒剑。\n\n·对非石化状态的角色全体造成魔法伤害（大）\n　使其短时间石化。\n　对石化状态的角色全员造成最大生命值\n　80%的伤害。\n·对前方三名角色造成魔法伤害（小）。\n　并且因该技能伤害\n　陷入中毒状态的角色会长时间石化。\n·对前方范围内的角色造成魔法伤害（中）\n　并且使其变为中毒状态。\n　自身的生命值降到50％以下时，会对敌方全体\n　做出反击，召唤出可可。\n·可可会对前方范围内物理攻击力\n　最高的一名角色造成物理伤害（小），\n　并且使其陷入中毒状态。\n　当自身的生命值处于最大生命的35%以下时，\n　中幅提升物理攻击力最高的己方同伴的技能值，\n　自身变为无法战斗状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307201, /*unit_name*/'巴吉里斯克', /*prefab_id*/307201, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】随身带着一把石化咒剑的怪鸟。以尾蛇的毒牙\n腐蚀猎物，以咒剑的石化阻止冒险者前进。\n\n·连结爆发时，对所有非处于无法行动状态的角色\n　造成魔法伤害（大），\n　并使其陷入石化状态。\n　对所有处于无法行动状态的角色造成\n　最大生命值80%的伤害。\n　同时解除敌方全体的属性值提升效果。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对前方三名角色造成魔法伤害（小）。\n　若有处于毒状态的角色因该技能而受到伤害，\n　则使其陷入石化状态。\n·对前方范围内的角色造成魔法伤害（中），\n　并使其陷入毒状态。');
