INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307804, /*unit_name*/'雨神大人', /*prefab_id*/307801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】沉浸在悲叹中的雨吉\n抗拒着众人的想法，又因其过于焦急\n甚至连自己心念已久的晴吉的声音\n都听不到了。\n\n·展开屏障，完全防御敌人的攻击。\n　屏障的耐久与伤害量无关，\n　只要被攻击命中就会减少耐久度，\n　受到200次攻击后，屏障就会被\n　破坏。\n·必杀技能对敌方全体造成魔法伤害（大），\n　并击飞敌人。同时，对前方范围内的所有敌人\n　造成追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307805, /*unit_name*/'雨神大人', /*prefab_id*/307800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】哀泣的雨伴随着悲伤\n不断降下。那漆黑的雨滴如悲哀的渴求\n侵蚀着人们的身心。\n\n·在战斗开始时召唤诅咒水晶。\n　场上存在诅咒水晶期间，\n　会对敌方全体造成持续伤害。同时，\n　因某种效果而回复生命值时，\n　回复效果将会无效，并受到原本回复量10%的\n　伤害。\n·必杀技能对敌方全体造成魔法伤害（大），\n　并击飞敌人。同时，对前方范围内的所有敌人\n　造成追加的魔法伤害（中）。\n·对前方范围内的所有敌人造成魔法伤害（中），\n　并小幅度击飞敌人。再对离自己最近的一名敌人\n　造成追加的魔法伤害（中）。\n·对最远的一名敌人造成魔法伤害（中），\n　并小幅度拉近目标。');
