INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308005, /*unit_name*/'奄美魔物', /*prefab_id*/308000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】现身于凌晨的跋扈魑魅，\n终于露出了要吞噬世间一切灵魂的獠牙。\n\n·奄美魔物会根据保有的“吸魂”属性值的\n　层数，强化自身的普通攻击与\n　各种技能的效果。\n·每发动一次普通攻击就能获得6层“吸魂”。\n·必杀技能对敌方全体造成\n　魔法伤害（特大）。并获得5层“吸魂”。\n·对敌方全体造成魔法伤害（小），并降低其技能值，\n　再展开一个能降低技能值上升量的\n　领域。技能值的减少量与领域的效果值，\n　会随奄美魔物保持的“吸魂”层数\n　而强化。\n·每次进行攻击，奄美魔物便获得1层“吸魂”\n　并召唤3只分身，再小幅度击飞\n　所有敌人。\n·奄美魔物每受到一次暴击伤害\n　便减少奄美魔物2层“吸魂”。\n·每当分身受到暴击伤害时，\n　便减少奄美魔物1层“吸魂”。\n·当剩余时间还有30秒时，\n　它的攻击会变得更为凶暴。\n　并获得20层“吸魂”。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308006, /*unit_name*/'蝮蛇恶灵', /*prefab_id*/308000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】以善良的灵魂为食的凶狠恶灵。\n为了寻求更好的祭品，会分出更多邪恶的身躯。\n\n·蝮蛇恶灵会根据保有的“吸魂”属性值的\n个数，强化自身的普通攻击\n　和各技能的效果。\n·每发动一次普通攻击就能获得6个“吸魂”。\n·连结爆发时，根据角色的最大生命值，\n　对敌方全体造成一定比例的魔法伤害，\n　并增加5个“吸魂”。\n·对敌方全体造成魔法伤害（小）的同时减少其技能值，\n　并展开降低技能值上升量的\n　领域。技能值减少量和领域的效果值\n　会随着蝮蛇恶灵保有的“吸魂”个数\n　增多而强化。\n·每进行一次攻击，蝮蛇恶灵便获得1个“吸魂”\n并召唤3只分身，同时将敌方全体\n　小幅度击飞。\n·蝮蛇恶灵每受到一次暴击伤害，\n　蝮蛇恶灵的“吸魂”都会减少2个。\n·分身每受到一次暴击伤害，\n　蝮蛇恶灵的“吸魂”都会减少1个。\n·蝮蛇恶灵自身的生命值低于30%时，\n　会展开更为激烈凶暴的攻击。\n　并获得20个“吸魂”。');
