INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308204, /*unit_name*/'剧毒曼德拉草', /*prefab_id*/308204, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】释放剧毒孢子，将大地\n化为荒野。\n以无数的尸骸为温床，遍地绽放\n剧毒的鲜花。\n\n按顺序重复以下行动。\n·埋入地面期间，获得不会受到\n　伤害的状态。\n·对敌方全体赋予强大的毒状态。\n·解除自身的属性值降低效果后，\n　极大幅度降低自身的物理防御力\n　与魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308205, /*unit_name*/'剧毒曼德拉草', /*prefab_id*/308200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】带有剧毒的粘液从触手上滴落而下。\n仅仅一滴便能让泉水干涸，令森林腐化，威胁生命。\n\n·对前方两名角色造成物理伤害，\n　并使其陷入毒状态。\n\n·连结爆发时，对敌方全体造成物理伤害（大），\n　并根据造成的\n　累计伤害量，回复自身的生命值。\n　大幅提升处于毒状态中的敌人的伤害量。\n·持续对敌方全体造成物理伤害（小），\n　并降低其物理防御力3次。\n·提升自身的物理攻击力的同时，\n　增加技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308206, /*unit_name*/'剧毒曼德拉草', /*prefab_id*/308200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】绝命的咆哮，巨腕的蹂躏。\n如花朵般凋零的生命。为自己即将迎来的终结献上摇篮曲，\n亡骸就此永眠。\n\n·根据最大生命值，对前方两名角色造成一定比例的\n　物理伤害。\n\n·连结爆发时，对敌方全体造成物理伤害（大），\n　并根据造成的\n　累计伤害量，回复自身的生命值。\n·持续对敌方全体造成物理伤害（小），\n　并降低其物理防御力3次。\n·提升自身的物理攻击力的同时，\n　增加技能值。\n·当剩余时间少于40秒时，会展开更为激烈凶暴的\n　攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308300, /*unit_name*/'岛鲸', /*prefab_id*/308300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】吞噬了大量探险家的幻岛巨鲸。冷酷无情地送葬深陷欲望与幻想的愚者。\n\n·根据角色的最大生命值，对敌方全体\n　造成物理伤害（大）。\n·对敌方全体造成魔法伤害（中），并极大幅度提升\n　岛鲸自身的物理防御力和魔法防御力。\n·对前方三名角色造成魔法伤害（大），\n　并将其大幅度击飞。\n·岛鲸自身的生命值低于40%时，\n　会展开更为激烈凶暴的攻击。');
