INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308405, /*unit_name*/'幻影男爵', /*prefab_id*/308400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】冥府之门悄然敞开，\n生者之魂堕入深渊。再也无法回来的灵魂，\n只能以思念为印记，游荡在黑暗之中。\n\n·连结爆发时，根据角色的最大生命值，\n　对敌方全体造成一定比例的魔法伤害，\n　并降低其魔法防御力。\n\n·对距离最远的一名敌人造成物理伤害（中），\n　并将其拉至距离最近的角色的位置，\n　同时使其眩晕。\n·对前方三名角色造成魔法伤害（中），\n　中幅度降低其技能值的同时\n　中幅度回复幻影男爵的技能值。\n　并且解除使用物理攻击的前三名角色的\n　属性值提升效果，\n　对使用魔法攻击的前三名角色造成\n　追加的魔法伤害（大）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308406, /*unit_name*/'幻影男爵', /*prefab_id*/308400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】与挚爱离别的时刻正在逼近。\n为了反抗悲哀的宿命，\n怨灵发出了临死前的恸哭。\n\n·连结爆发时，根据角色的最大生命值，\n　对敌方全体造成一定比例的魔法伤害，\n　并降低其魔法防御力。\n·对距离最近的一名敌人造成魔法伤害（大）。\n　同时根据角色的最大生命值，\n　对前方三名角色造成一定比例的魔法伤害，\n　并大幅度将其击飞。\n\n·对距离最远的一名敌人造成物理伤害（中），\n　并将其拉至距离最近的角色的位置，\n　同时使其眩晕。\n·战斗开始后，提升自身的魔法攻击力\n　和行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308500, /*unit_name*/'水瑚麟', /*prefab_id*/308500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】被月神魔力所魅惑，变得狂暴的水之神兽。\n忘记了自己的使命，化为吞噬万物的狂兽。\n\n·对敌方全体造成魔法伤害（大），并赋予畏惧状态。\n　陷入畏惧状态的角色，在一段时间内\n　必杀技能的攻击力与回复生命值效果都将被减半。\n·对前方范围内的敌人造成魔法伤害（中），\n　并根据击中次数，小幅提升自身的\n　物理防御力。同时，展开一个能造成\n　魔法伤害的领域。\n·对前方一名角色造成魔法伤害（大），\n　并消耗自身的生命值，对敌方全体造成追加的\n　魔法伤害（中）。\n　同时，小幅提升自身的物理攻击力与\n　魔法攻击力。');
