INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308604, /*unit_name*/'类龙生物', /*prefab_id*/308600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】自苍天降下的咆哮撕裂了天空，\n伪龙在阳光下展开自傲的双翼，降临于此。\n\n·类龙生物的生命值低于80%时，\n　会进入下一模式。\n\n·在战斗开始时，获得70个“火焰果”。\n·连结爆发时，根据类龙生物\n　保有的“火焰果”个数，对敌方全体造成\n　一定比例的物理伤害。\n　随后减少60个“火焰果”。\n\n·根据角色的最大生命值，对前方三名角色造成一定比例的\n　物理伤害，并使其眩晕。\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\n　在类龙生物吃东西时，\n　最多可持续获得60个“火焰果”。\n　如果在吃东西的时候，\n　类龙生物受到了150000以上的伤害，该技能中断，\n　并减少30个“火焰果”。\n·召唤4只怪物，并小幅度击飞\n　敌方全体。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308605, /*unit_name*/'类龙生物', /*prefab_id*/308600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】在伪龙的呼唤下，森林里的\n魔物集结于此，拼尽全力地保护它。\n\n·打倒魔像、树精、\n　毒食花后，进入下一模式。\n\n·类龙生物对眼前的一名敌人进行\n　物理攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308606, /*unit_name*/'类龙生物', /*prefab_id*/308606, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】对龙的憧憬使蜥蜴觉醒了。空中霸者的吐息将化为焚毁这座小岛的\n劫火。\n\n·在战斗开始时，获得70个“火焰果”。\n·连结爆发时，根据类龙生物\n　保有的“火焰果”个数，对敌方全体造成\n　一定比例的物理伤害。\n　随后减少60个“火焰果”。\n\n·根据角色的最大生命值，对前方三名角色造成一定比例的\n　物理伤害，并使其眩晕。\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\n　在类龙生物吃东西时，\n　最多可持续获得60个“火焰果”。\n　如果在吃东西的时候，\n　类龙生物受到了250000以上的伤害，该技能中断，\n　并减少30个“火焰果”。\n·战斗开始后，会展开更为激烈凶暴的\n　攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309000, /*unit_name*/'陆生树懒', /*prefab_id*/309000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】贪睡在梦境与现实之间的树懒。\n被打扰清梦时会大发雷霆，用超能力大肆破坏。\n·根据角色最大生命值，对敌方全体造成\n　一定比例的魔法伤害，并大幅度降低物理攻击力\n　和魔法攻击力。\n　这个技能的异常状态在伤害免疫的情况下\n　也会成功赋予。\n·对敌方全体造成魔法伤害（小）。');
