INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309306, /*unit_name*/'复仇驯鹿', /*prefab_id*/309300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】负伤的巨兽点燃了\n反击的狼烟。完全陷于憎恶与愤怒中，将变得\n残忍无比。\n\n·必杀技能会在召唤角色被消灭后，\n　根据角色的最大生命值\n　对敌方全体造成物理比例伤害。\n　承受该伤害后，生命值低于35%的角色\n　在一定时间内变成驯鹿，\n　且物理暴击率\n　和魔法暴击率降低。变成驯鹿后的角色，\n　只能使用普通攻击。\n　一段时间内，自身的物理防御力大幅降低。\n·以最前方的角色为中心，根据角色的最大生命值\n　对一定范围内的角色\n　造成物理比例伤害。并提升自己的\n　物理攻击力。\n·对最前方的角色造成物理伤害，\n　将其击退的同时使其眩晕。\n·战斗开始后，会展开更为激烈凶暴的\n　攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309400, /*unit_name*/'侍从·刀刃（物理）', /*prefab_id*/309400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】霸瞳皇帝创造的移动力量储藏库。\n掌管力量的权能，其巨大的剑戟可斩破一切。\n\n·眩晕与击飞有效。\n\n·必杀技能会对敌方全体造成与角色\n　最大生命值相应的物理百分比伤害。\n\n·以眼前一名敌人为中心，对范围内的所有敌人\n　造成与角色的最大生命值相应的\n　物理百分比伤害。\n\n·当侍从·刀刃（物理）的生命值\n　低于40%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309500, /*unit_name*/'侍从·魔能（魔法）', /*prefab_id*/309500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】霸瞳皇帝创造的移动力量储藏库。\n掌管魔法的权能，其无穷的魔力可毁灭一切。\n\n·必杀技能会对敌方全体造成与角色\n　最大生命值相应的魔法百分比伤害。\n　同时，小幅降低其物理攻击力与魔法攻击力。\n\n·对随机一名敌人造成魔法伤害，\n　并赋予诅咒、麻痹状态，大幅降低其技能值。\n\n·当侍从·魔能（魔法）的生命值\n　低于40%时，会变得更加凶暴，\n　攻击更为猛烈。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309600, /*unit_name*/'侍从·洛克斯（多部位）', /*prefab_id*/309600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】霸瞳皇帝创造的移动力量储藏库。\n掌管责任与义务的权能，可屠戮广阔领域中的一切。\n\n·必杀技能会以眼前一名敌人为中心，\n　对范围内的所有敌人造成与角色最大生命值\n　相应的物理百分比伤害，\n　并对眼前一名敌人造成物理伤害（小）。\n\n·以眼前一名敌人为中心，造成与\n　角色最大生命值相应的\n物理百分比伤害。\n\n·当侍从·洛克斯（多部位）的生命值\n　低于40%时，会变得更加顽强，\n　进一步提升自身的防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309700, /*unit_name*/'侍从·铁壁（防卫）', /*prefab_id*/309700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】霸瞳皇帝创造的移动力量储藏库。\n掌管守护的权能，其坚固的铁壁可防御一切。\n\n·必杀技能会大幅提升自身的\n　物理防御力与魔法防御力。\n\n·以眼前一名敌人为中心，对范围内的所有敌人\n　造成魔法伤害。同时，小幅提升自身的物理防御力\n　和魔法防御力。\n\n·当侍从·铁壁（防卫）的生命值\n　低于40%时，会变得更加顽强，\n　进一步提升自身的防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309800, /*unit_name*/'霸瞳皇帝', /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.6, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/'王都最终决战_基座单元');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309801, /*unit_name*/'霸瞳皇帝', /*prefab_id*/309801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/'【模式1】抗拒着一切的悲伤公主。\n以神自居，沉浸于不详的力量之中。\n并向整个阿斯特朗举起了狂乱的刀刃。\n就用无数的剑刃，来穿破守护着自欺欺人的公主的铁壁吧。\n\n·展开屏障，完全防御敌人的攻击。\n　屏障的耐久与伤害量无关，\n　只要被攻击命中就会减少耐久度，\n　受到280次攻击后，屏障就会被\n　破坏。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309802, /*unit_name*/'霸瞳皇帝', /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/'【模式2】对渺小之人的反抗表现出些许的急躁。\n将以自己的大义去制裁那些心怀希望、阻挡自己的人。\n举起那柄悲愤之剑，将其灵魂化为尘土。\n以正义的魔法，去对抗伪神所挥出的\n漆黑之刃吧。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对眼前一名敌人造成物理伤害（特大），\n　并对其周围的所有敌人造成魔法伤害（中），\n　使其昏迷。昏迷的角色\n　在受到下次攻击前，都无法行动。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309803, /*unit_name*/'霸瞳皇帝', /*prefab_id*/309800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/'【模式3】创世神发出了临终的呼声。\n那因怨恨而吐露的拒绝之语将化为诅咒，\n折磨众人的身体、精神、灵魂，让一切回归黑暗。\n此刻即为决战。各位，举起剑来，用自己的双手\n开拓明天吧。\n\n·必杀技能会根据敌方全体的剩余生命值\n　造成特大百分比伤害。\n\n·对眼前一名敌人造成物理伤害（特大），\n　并对其周围的所有敌人造成魔法伤害（中），\n　使其昏迷。昏迷的角色\n　在受到下次攻击前，都无法行动。\n\n·对敌方全体造成物理伤害（小）与\n　魔法伤害（小）。\n\n·以最后一名敌人为中心，展开中幅降低\n　物理防御力与与魔法防御力的领域，\n　并造成魔法伤害（小），\n　同时赋予诅咒状态。诅咒所造成的持续伤害\n　会逐秒增加。\n\n·在战斗开始后，大幅提升自身的\n　行动速度，并小幅提升自身的\n　魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309804, /*unit_name*/'霸瞳皇帝', /*prefab_id*/309804, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/309800, /*cutin_star6*/309800, /*visual_change_flag*/0, /*comment*/'显示地图用');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309900, /*unit_name*/'专家哥布林', /*prefab_id*/309900, /*motion_type*/0, /*se_type*/17, /*move_speed*/270, /*search_area_width*/1100, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】企图将塔的魔力占为己有的哥布林学者。\n用禁术操纵泥塑人偶，强行掠夺敌人的自由。\n\n·对敌方全体造成魔法伤害（大），\n　让所有召唤体、分身即死。\n·对前方大范围造成与生命值相应的\n　百分比物理伤害（中），并大幅击飞敌人。\n　同时，召唤1只污泥人偶。\n　污泥人偶会对前方第一名或第二名敌人\n　造成物理伤害，并降低其行动速度。\n·以前方第二名敌人为中心，造成小范围的\n　物理伤害（中），并赋予猛毒、眩晕状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310000, /*unit_name*/'山神大人', /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害，\n　并将其击退。\n　对最前方的角色追加物理伤害（特大），\n　并大幅击飞对方，\n　使其眩晕。\n　山神大人每使用一次必杀技能，\n　自身的物理防御力都会降低。\n　这个效果可以累积。\n·对魔法攻击力最高的1名角色，\n　根据其最大生命值造成魔法比例伤害，\n　并大幅度降低其技能值。\n·提升自身的技能值和行动速度。\n·山神大人的生命值低于55%时，\n　会展开更为激烈凶暴的攻击，\n　但自身的物理防御力也会降低。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310001, /*unit_name*/'山神大人', /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】传说拥有迅捷速度的山中土地神。\n自太古时代便为了寻求劲敌，而每日奔波着。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害，\n　并将其击退。\n　对最前方的角色追加物理伤害（特大），\n　并大幅击飞对方，\n　使其眩晕。\n　山神大人每使用一次必杀技能，\n　自身的物理防御力都会降低。\n　这个效果可以累积。\n·提升自身的技能值和行动速度。');
