INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310004, /*unit_name*/'山神大人', /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】试探冒险者的实力，召唤内在的\n影子。寒风凛冽，天下险峻，自身的脆弱\n将受到考验。\n\n·对最前方的角色造成物理伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310005, /*unit_name*/'山神大人', /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】男子汉的疾驰可震撼大地。\n仿佛是为了庆祝这幅盛景，目出鲷也飞舞于高山之上。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害，\n　并将其击退。\n　对最前方的角色追加物理伤害（特大），\n　并大幅击飞对方，\n　使其眩晕。\n　山神大人每使用一次必杀技能，\n　自身的物理防御力都会降低。\n　这个效果可以累积。\n·对魔法攻击力最高的2名角色，\n　根据其最大生命值造成魔法比例伤害，\n　并大幅度降低技能值。\n·在后方展开一个降低物理防御力的领域，\n　并对敌方全体造成物理伤害（中）。\n　目出鲷离开后背期间，特大幅度降低\n　山神大人的魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310006, /*unit_name*/'山神大人', /*prefab_id*/310000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】给予向神发起竞争的强者最后的试炼。\n拼上彼此的一切，奔向思念之人。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害，\n　并将其击退。\n　对最前方的角色追加物理伤害（特大），\n　并大幅击飞对方，\n　使其眩晕。\n　山神大人每使用一次必杀技能，\n　自身的物理防御力都会降低。\n　这个效果可以累积。\n·对魔法攻击力最高的1名角色，\n　根据其最大生命值造成魔法比例伤害，\n　并大幅度降低其技能值。\n·提升自身的技能值和行动速度。\n·战斗开始后，会展开更为激烈凶暴的攻击，\n　同时会降低自身的\n　物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310100, /*unit_name*/'愤怒巨龙', /*prefab_id*/310100, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】栖息在忘却的秘境里的年轻巨龙。\n用灼热的业火烧光闯入禁忌的冒险者。\n\n·必杀技能会对敌方全体造成\n　物理比例伤害，并降低其技能值。\n·战斗开始时，特大幅度降低自身的技能值。\n·以前方的1名角色为中心，对一定范围内的角色\n　造成魔法伤害，\n　并降低其物理防御力。\n·对敌方全体造成物理伤害，\n　并使其陷入烧伤状态。\n·对敌方全体造成物理伤害，击退对方\n　并解除属性值提升效果。\n\n·所有目标被击破时，特大幅度降低\n　自身技能值。\n·愤怒巨龙自身剩余的生命值低于10%时，\n　会展开更为激烈凶暴的攻击，\n　且无法被击破。\n·若战斗超时，会使敌方全体陷入\n　无法战斗状态。\n');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310200, /*unit_name*/'硬头鲨', /*prefab_id*/310200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】作为海蚀洞的支配者君临于此的鲨鱼魔物。\n将凶恶的欲望倾注到捕食中的海中大恶徒。\n\n·对敌方全体造成物理伤害（特大），并降低其物理防御力与魔法攻击力。\n\n·对前方范围内的所有敌人造成魔法伤害（小），并降低其技能值与技能值上升量。');
