INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310804, /*unit_name*/'忍者头领半藏', /*prefab_id*/310801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】召唤【伊丽莎白牧场】的\n暗影。用幻术制造出的四农士的幻影\n再次使大家的归宿陷入困境。\n\n·对眼前的1名敌人造成物理伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310805, /*unit_name*/'忍者头领半藏', /*prefab_id*/310800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】用秘传的通灵术变身成忍蛙，\n向周围喷射蛤蟆的猛毒。这种猛毒\n只要吸一口就会腐蚀全身，慢慢地使身体溃烂。\n\n·战斗开始后，监察官登场。监察官受到\n　伤害后，会特大幅度降低\n　忍者头领半藏的物理防御力和\n　魔法防御力。\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害。\n·变成大蛤蟆吐出猛毒，使敌方全体陷入\n　猛毒状态。此攻击必定命中。\n·对敌方全体造成物理伤害（中），\n　并使其陷入眩晕。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310806, /*unit_name*/'忍者头领半藏', /*prefab_id*/310800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】最后，找回身为忍者的尊严，\n发挥自己真正的力量。紫电一闪，\n不等发出惨叫就化作了尸体。\n\n·战斗开始后，提升自身的技能威力。\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害。\n·变成大蛤蟆吐出猛毒，使敌方全体陷入\n　猛毒状态。此攻击必定命中。\n·对敌方全体造成物理伤害（中），\n　并使其陷入眩晕。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310900, /*unit_name*/'袭击兰德索尔各地的神秘飞碟', /*prefab_id*/310900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/0.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】突袭和平的丰收节的神秘飞行物。\n突然出现在兰德索尔各地，进行破坏和掠夺。\n\n·必杀技能会对敌方全体造成物理伤害（大）。\n·对前方1名角色造成物理伤害（小）。\n·对剩余生命值最高的1名角色造成物理伤害（小）。\n·随机对1名角色造成物理伤害（小）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311000, /*unit_name*/'城堡树精', /*prefab_id*/311000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】因塔的魔力发生变异而化为要塞的巨大魔树。\n把大地的力量变成炮弹，以坚固的身躯\n将一切破坏殆尽。\n\n·对敌方全体造成物理伤害（大）。\n·对一定范围内的所有敌人造成持续的魔法伤害，\n　并展开可以降低物理防御力的领域。\n·以后方第2名角色为中心，对其小范围内的角色\n　造成物理伤害（中），并降低其技能值。\n　之后，回复己方全体的技能值。');
