INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311301, /*unit_name*/'觉醒·外遇星', /*prefab_id*/311300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】附身在空荡荡的心灵空隙处的\n星之精灵。用过剩的自我意识和单纯的私心\n来迷惑对方。\n\n·必杀技能会根据外遇星持有的【异心】数量，\n　对敌方全体造成\n　魔法比例伤害。\n　外遇星的剩余生命值低于50%时，\n　会提升造成的伤害。\n　造成伤害后，外遇星持有的\n　【异心】数量会变为0。\n·以最后方的敌人为中心，对一定范围内的敌人\n　造成魔法伤害，使其陷入诱惑状态，\n　提升物理攻击力和魔法攻击力。\n　根据用该技能造成伤害的敌人的数量，\n　外遇星在一定时间内增加【异心】的数量。\n·对前方范围内的敌人造成魔法伤害，\n　使其陷入诱惑状态，并提升物理攻击力\n　和魔法攻击力。\n　根据用该技能造成伤害的敌人的数量，\n　外遇星在一定时间内增加【异心】的数量。');
