INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311501, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】由于过大的纯粹愿望而暴走的圣弓。\n如果被它射出的箭射穿，就会化为无力的天使。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害，\n　对天使状态的角色赋予诅咒。\n　该效果所造成的诅咒伤害，\n　无法回复技能值。\n·对生命值最低的3名角色造成魔法比例伤害，\n　使生命值低于60%的角色在一定时间内\n　变成天使。\n　变成天使的角色只能\n　使用普通攻击，身上的属性值提升效果\n　被解除。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311502, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】由于过大的纯粹愿望而暴走的圣弓。\n如果被它射出的箭射穿，就会化为无力的天使。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害，\n　对天使状态的角色赋予诅咒。\n　该效果所造成的诅咒伤害，\n　无法回复技能值。\n·对生命值最低的3名角色造成魔法比例伤害，\n　使生命值低于60%的角色在一定时间内\n　变成天使。\n　变成天使的角色只能\n　使用普通攻击，身上的属性值提升效果\n　被解除。\n·对生命值最高的2名角色造成魔法伤害（中），\n　并降低其行动速度。\n·圣洁之弓自身的生命值低于50%时，\n　会提升魔法攻击力，并展开\n　更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311503, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】由于过大的纯粹愿望而暴走的圣弓。\n如果被它射出的箭射穿，就会化为无力的天使。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害，\n　对天使状态的角色赋予诅咒。\n　该效果所造成的诅咒伤害，\n　无法回复技能值。\n·对生命值最低的3名角色造成魔法比例伤害，\n　使生命值低于60%的角色在一定时间内\n　变成天使。\n　变成天使的角色只能\n　使用普通攻击，身上的属性值提升效果\n　被解除。\n·对生命值最高的2名角色造成魔法伤害（中），\n　并降低其行动速度。\n·圣洁之弓自身的生命值低于50%时，\n　会提升魔法攻击力和技能值，并展开\n　更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311504, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】扭曲的愿望变成结晶，让人们\n疯狂。这结晶将封杀一切进攻。\n\n·战斗开始时唤出邪念水晶。\n·邪念水晶在战斗中存在的期间，\n　会大幅度提升圣洁之弓的物理防御力\n　和魔法防御力。\n·邪念水晶一旦被破坏，\n　圣洁之弓的物理防御力和魔法防御力\n　会大幅度降低。\n·对生命值最高的2名角色造成魔法伤害（中），\n　并降低其行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311505, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】召唤依里和茜里的暗影。\n自己的负面形象，考验着双胞胎之间真正的羁绊。\n\n·对生命值最低的3名角色造成魔法比例伤害，\n　使生命值低于60%的角色在一定时间内\n　变成天使。\n　变成天使的角色只能\n　使用普通攻击，身上的属性值提升效果\n　被解除。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311506, /*unit_name*/'失控的圣洁之弓', /*prefab_id*/311500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】受困于无法实现的愿望，圣弓的权能\n达到了极致。解放的力量震撼天地，\n消灭一切邪念。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害，\n　对天使状态的角色赋予诅咒。\n　该效果所造成的诅咒伤害，\n　无法回复技能值。\n·对生命值最低的3名角色造成魔法比例伤害，\n　使生命值低于60%的角色在一定时间内\n　变成天使。\n　变成天使的角色只能\n　使用普通攻击，身上的属性值提升效果\n　被解除。\n·对生命值最高的2名角色造成魔法伤害（中），\n　降低其行动速度，并降低其\n　魔法防御力。\n·剩余时间少于35秒时，会提升魔法攻击力\n　和技能值，并展开更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311600, /*unit_name*/'叫怖的幽灵教父', /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】让人类感到恐惧的鬼怪们的首领。\n煽动手下，秘密策划扩大领地的规模。\n\n·必杀技能会根据角色原本的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\n　都无法恢复。');
