INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311604, /*unit_name*/'叫怖的幽灵教父', /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】为了寻找美味的灵魂，鬼怪们的首领\n再次现身。带着残暴的家族，\n蹂躏着人们，使城市充满了尖锐的悲鸣。\n\n·战斗开始时，召唤3只\n　幽灵手下。\n·每打倒1只幽灵手下，\n　剩下的幽灵手下数量就减少1只。\n　之后会再次召唤幽灵手下。\n·幽灵手下会减少眼前1名角色的\n　技能值，并回复幽灵教父的\n　技能值。\n·必杀技能会根据角色原本的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\n　都无法恢复。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311605, /*unit_name*/'叫怖的幽灵教父', /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】对破坏领地的一切进行制裁。\n一旦踏入他的领地，就会有魑魅魍魉\n蛀蚀他的生命，让他遭受人间地狱之苦。\n\n·战斗开始时，角色的最大生命值\n　减少35%。减少的生命值直到战斗结束\n　都无法恢复。\n·必杀技能会根据角色原本的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\n　都无法恢复。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311606, /*unit_name*/'叫怖的幽灵教父', /*prefab_id*/311600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】将积蓄的力量全部倾吐出来，释放\n真正的力量。为了统治这个世界，他残暴无道至极，\n将其渲染成地狱。\n\n·必杀技能会根据角色原本的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\n　都无法恢复。\n·为自身展开物理/魔法无效化屏障。\n·幽灵教父自身的生命值低于37.5%时，\n　会展开更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311700, /*unit_name*/'幽灵兄弟', /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n以自身为盾，保护首领免受一切攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311701, /*unit_name*/'幽灵兄弟', /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n用锻炼过的炮弹向城市发起特攻。血气方刚地\n冲锋陷阵，让四处逃窜的人们陷入恐怖的深渊。\n\n·幽灵教父不会成为攻击的对象。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311702, /*unit_name*/'幽灵兄弟', /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n把和炮弹们收取来的优质精气献给首领。\n\n·幽灵教父不会成为攻击的对象。\n·回复幽灵教父的技能值。\n　幽灵教父陷入无法行动状态时，\n　不回复技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311703, /*unit_name*/'幽灵兄弟', /*prefab_id*/311700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'终于到了出场的时候，气喘吁吁地登场。\n气势十足的最强盾牌，挫败了对手的心。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311800, /*unit_name*/'幽灵兄弟', /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n将收取的精气交给首领，负责辅佐。\n\n·回复幽灵教父的技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311801, /*unit_name*/'幽灵兄弟', /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n守护在首领的附近，不露出一丝破绽。在背地里\n暗中活动，试图将城市中的人逼入绝望之中。\n\n·幽灵教父不会成为攻击的对象。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311802, /*unit_name*/'幽灵兄弟', /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'幽灵教父的贴身手下。\n把和部下收取来的优质精气献给首领。\n\n·幽灵教父不会成为攻击的对象。\n·回复幽灵教父的技能值。\n　幽灵教父陷入无法行动状态时，\n　不回复技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311803, /*unit_name*/'幽灵兄弟', /*prefab_id*/311800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/999.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'为了守护家族的危机，冷静地\n观察战况。作为首领忠实的左膀右臂，直到死前\n都贯彻辅佐任务。\n\n·回复幽灵教父的技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/311900, /*unit_name*/'洁白独角兽', /*prefab_id*/311900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】吞食雾霭，自身的姿态也如雾霭\n一般难以捉摸。讨厌人类，\n会毫不留情地消灭靠近的人。\n\n·对敌方全体造成\n　魔法伤害（大）。此外，提升对烧伤状态的敌人\n　造成的伤害。\n\n·以物理攻击力最高的敌人为中心，\n　对一定范围内造成魔法伤害（中），\n　并使其陷入烧伤状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312000, /*unit_name*/'黑曜双足飞龙', /*prefab_id*/312000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】宿有魔力的黑曜石的结晶龙。据说美丽身躯的光辉\n是彷徨在冥府的灵魂的哀叹。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于60％时，大幅降低\n　其技能值。\n·特大幅度降低敌方全体的吸血生命值吸收，并大幅降低\n　其物理防御力。\n·对魔法攻击力最高的1名角色造成\n　物理伤害（大）。\n　·并以魔法攻击力最高的1名角色为中心，\n　对一定范围内造成物理伤害（中），\n　中幅降低其技能值。');
