INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312105, /*unit_name*/'反物质兽 de M', /*prefab_id*/312100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.7, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】吸收了魔法少女们的猛攻，\n反物质兽的欲望变得更加膨胀。甚至，\n保护人们的勇气也被溶蚀。\n\n·必杀技能会解除敌方全体的屏障，\n　对其造成魔法伤害（大）。并大幅降低其物理防御力\n　和魔法防御力，一定时间内生命值回复量\n　降低70%。\n·解除敌方全体的屏障，根据最大生命值\n　对其造成魔法比例伤害，并使其陷入中毒状态。');
