INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312306, /*unit_name*/'巨大金枪鱼', /*prefab_id*/312302, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】即使无数次尝试用鱼叉叉住那黑色的巨大身体，\n也无法阻挡传说中的巨鱼的猛冲。\n与长久以来在海上奋斗的男儿们的决胜时刻到了。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害。\n　并将其击退。\n·提升自身的物理攻击力、行动速度\n　和技能值。\n·剩余时间少于35秒时，会展开更为激烈凶暴的\n　攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312400, /*unit_name*/'寒冰格里芬', /*prefab_id*/312400, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】展开冰翼，威吓冒险者的\n大鹫。闯入地盘的人，\n会被无情地冻住，成为孩子们的\n祭品。\n\n·必杀技能会对前方小范围内\n　造成物理伤害（大），\n　并大幅度将其击飞，\n　使其陷入冻结状态。');
