INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312704, /*unit_name*/'黑风的精灵', /*prefab_id*/312700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.7, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】第一试炼。只要能看清黑风精灵暴风的缝隙，\n就能自然而然地开拓出\n自己前进的道路。\n\n·对敌方全体造成物理伤害，使其眩晕\n　并将其击退。\n　黑风的精灵沉睡时，会大幅降低\n　自身的物理防御力，如果在此期间对其造成\n　100000以上的伤害，可延长其防御力降低的时间，\n　并降低其行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312705, /*unit_name*/'黑风的精灵', /*prefab_id*/312705, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.7, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】第二试炼。与自己软弱的内心\n对峙吧。答案总是在自己心中。暂且相信同伴的力量，\n去面对吧。\n\n·对前方2名角色造成物理伤害（小），\n　将其击退的同时使其眩晕。');
