INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/312900, /*unit_name*/'黑月的守护石像', /*prefab_id*/312900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】守护天上圣域，被黑月祝福的雕像。\n将闯入圣域的冒险者埋葬在黄泉的黑暗中。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成物理比例伤害。\n·对敌方全体造成物理伤害（大），\n　并解除属性值提升效果。在伤害免疫的情况下，\n　该技能也会生效。\n·大幅提升自身物理攻击力和\n　魔法攻击力。白阳的守护石像被讨伐时，\n　使用此技能。\n·黑月的守护石像的生命值低于50%时，\n　大幅提升其物理攻击力，中幅提升其\n　物理防御力。\n·黑月的守护石像被击破时，\n　小幅降低其物理防御力\n　和魔法防御力。\n·白阳的守护石像被讨伐时，\n　战斗开始后，或是在黑月的守护石像进行\n　普通攻击时，改变黑月的守护石像的\n　攻击行动。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313000, /*unit_name*/'白阳的守护石像', /*prefab_id*/313000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】守护天上圣域，被白阳祝福的雕像。\n用净化一切的白雷，审判冒险者。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n·对敌方全体造成魔法伤害（大），\n　并解除属性值提升效果。白阳的守护石像\n　大幅提升魔法攻击力。在伤害免疫的情况下，\n　该技能也会生效。黑月的\n　守护石像被讨伐时，使用\n　此技能。\n·中幅提升我方全体的物理防御力和魔法\n　防御力。\n·白阳的守护石像的生命值低于50%时，\n　特大幅度提升其魔法攻击力，小幅提升其\n　魔法防御力。\n·白阳的守护石像被击破时，\n　小幅降低其物理防御力和魔法防御力。\n·黑月的守护石像被讨伐时，\n　战斗开始后，或是在白阳的守护石像进行\n　普通攻击时，改变白阳的守护石像的\n　攻击行动。');
