INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313201, /*unit_name*/'无名艺术', /*prefab_id*/313200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】表现出了制作者的深渊？异形艺术作品的\n魔物。据制作者称，以常人的尺度是无法理解\n其风采的，懂的人才懂。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于50%时，\n　会使其陷入烧伤状态。\n　此技能赋予的烧伤伤害，\n　不会提升技能值。\n·以前方第3名角色为中心，\n　根据最大生命值对一定范围内的角色造成物理比例伤害。\n　·必杀技能会根据最大生命值对陷入烧伤状态的角色\n　追加物理比例伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313202, /*unit_name*/'无名艺术', /*prefab_id*/313200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】表现出了制作者的深渊？异形艺术作品的\n魔物。据制作者称，以常人的尺度是无法理解\n其风采的，懂的人才懂。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于50%时，\n　会使其陷入烧伤状态。\n　此技能赋予的烧伤伤害，\n　不会提升技能值。\n·以前方第3名角色为中心，\n　根据最大生命值对一定范围内的角色造成物理比例伤害。\n　·必杀技能会根据最大生命值对陷入烧伤状态的角色\n　追加物理比例伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313203, /*unit_name*/'无名艺术', /*prefab_id*/313200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】表现出了制作者的深渊？异形艺术作品的\n魔物。据制作者称，以常人的尺度是无法理解\n其风采的，懂的人才懂。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于50%时，\n　会使其陷入烧伤状态。\n　此技能赋予的烧伤伤害，\n　不会提升技能值。\n·以前方第3名角色为中心，\n　根据最大生命值对一定范围内的角色造成物理比例伤害。\n　·必杀技能会根据最大生命值对陷入烧伤状态的角色\n　追加物理比例伤害。\n·剩余时间少于30秒时，会小幅提升自身的技能值，\n　 并展开更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313204, /*unit_name*/'无名艺术', /*prefab_id*/313200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】拥有生命的黏土人偶不会停止。\n释放出内在的异形魔力，\n少女们和受过打击的校园能够承受住发出的光线吗？\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于50%时，\n　会使其陷入烧伤状态。\n　此技能赋予的烧伤伤害，\n　不会提升技能值。\n·解除前方范围内敌人的屏障，根据最大生命值\n　对其造成魔法比例伤害。\n　并使其陷入束缚状态。\n　不过，无名艺术受到中毒、猛毒、诅咒、烧伤效果时，\n　不会对其赋予束缚效果。\n·自身受到中毒、猛毒、诅咒、烧伤效果时，\n　特大幅度降低自己的物理、魔法防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/313205, /*unit_name*/'无名艺术', /*prefab_id*/313200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】黏土人偶所描绘的是\n设计独到的魔法阵。不要被它的外表所欺骗，\n稍不留神就会被瞬间压扁。\n\n·必杀技能会根据角色的最大生命值，\n　对敌方全体造成魔法比例伤害。\n　角色的生命值低于50%时，\n　会使其陷入烧伤状态。\n　此技能赋予的烧伤伤害，\n　不会提升技能值。\n·普通攻击时，会对前方1名角色造成\n　物理伤害（中）。\n　无名艺术持有的【扇子】数越多，\n　造成的伤害就越高。\n·以前方第3名角色为中心，\n　对一定范围的角色造成物理伤害（中）。\n　无名艺术持有的【扇子】数越多，\n　造成的伤害就越高。\n·战斗开始时，展开魔法阵。\n\n【魔法阵效果】\n·无名艺术每秒追加5个【扇子】。\n·无名艺术每次受到伤害时，\n　无名艺术的【扇子】都会减少1个。');
