INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/'梦魇牡羊', /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300301, /*unit_name*/'梦魇牡羊', /*prefab_id*/300301, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】获得强大力量的白羊座魔星兽。\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\n\n·对敌方全体造成魔法伤害（大），对束缚状态的\n　敌人额外造成魔法伤害（小）。\n·对前方范围内的敌人造成魔法伤害（中）。\n·对一名敌人施加束缚状态。\n·对剩余生命值最低的一名敌人赋予\n  嘲讽状态，并造成魔法伤害（小）。\n  这个技能的异常状态在伤害免疫的情况下\n  也会成功赋予。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300400, /*unit_name*/'摩羯教主', /*prefab_id*/300400, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'企图利用可疑邪教支配世界的摩羯座魔星兽。\n会引诱沙漠里的迷途之人，用恶魔之仪式将其变为俘虏。\n\n·必杀技能对敌方全体造成魔法伤害（大）\n　另外降低物理攻击角色的物理攻击力\n　降低魔法攻击角色的魔法防御力。\n·对物理攻击力最高的一名角色造成魔法伤害（特大），\n　并降低其技能值。\n·对前方两名敌人造成魔法伤害（小），并\n　赋予沉默状态。\n　并对敌方全体造成魔法伤害（中）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300401, /*unit_name*/'摩羯教主', /*prefab_id*/300401, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\n\n·必杀技能时，对敌方全体造成魔法伤害（大）\n　并降低使用物理攻击角色的物理攻击力\n　降低使用魔法攻击角色的魔法攻击力。\n·对前方一名敌人造成魔法伤害（特大）\n　并赋予沉默状态。\n　同时对敌方全体造成魔法伤害（中）。\n·对物理攻击力最高的一名敌人\n　造成魔法伤害（大），并降低其技能值。');
