INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/'兽人首领', /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】身为族长鼓舞群落，\n用足以粉碎大地的连击\n给敌人施加大量伤害的\n顶级兽人。\n\n·以随机敌人为中心，\n　对三名角色造成物理伤害（中），\n　并提升物理防御力。\n\n·提升己方全体的物理攻击力。');
