INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/'长老角鹿', /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】一旦爆发便无法对付，其嘶吼声据说能传到天际，传说中的狂野角鹿。\n\n·对敌方全体造成物理伤害（中），并小幅度击飞。\n\n·对前方三名角色造成物理伤害（中），并降低敌人的物理攻击力和物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/'灵魂角鹿', /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】由于特异力量而变异的狂野角鹿。\n将犄角中注入魔力，释放比打击更强力的一击。\n\n·给前方范围内的敌人施加高等魔法伤害，\n 将其击退的同时使其眩晕。\n·给前方范围的敌人施加低等魔法伤害，\n 同时降低其魔法防御与攻击速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/'暴君寿龟', /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】如岩山般进化的巨大寿龟。\n强力无比的头槌可以轻易地击碎大地。\n\n·必杀技能对前方范围内造成物理伤害，并使其麻痹。\n·降低前方3名角色的物理防御力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/'海龙', /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前三名敌人造成物理伤害（中）。\n\n·对前两名敌人造成物理伤害（中），并赋予冻结状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301001, /*unit_name*/'海龙', /*prefab_id*/301001, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前方三名敌人造成物理伤害（中）。\n·对前方一名角色造成物理伤害（中），并赋予冻结状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301002, /*unit_name*/'海龙', /*prefab_id*/301002, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\n\n·对前方三名敌人造成物理伤害（中）。\n·对前方两名角色造成物理伤害（中），并赋予冻结状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/'野蛮魔熊', /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】用不符体格的敏捷与咬噬攻击夺走对方的体力，用巨大的利爪撕裂敌人。残忍的森之统治者。\n\n·对前方四名角色造成物理伤害（大）。\n\n·对前方一名角色造成物理伤害（小），并降低其物理攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/'狂乱魔熊', /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'由于特异力量而变异的野蛮魔熊。\n注入怒气的强壮手臂将敌人连同地面一齐撕裂。\n\n·对前方范围内的敌人造成物理伤害（大），\n　并施加眩晕状态。\n·对前方的一名敌方角色造成物理伤害（小），\n　并中幅降低目标的物理攻击力。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/'巨型魔像', /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】能召唤出无限的魔像，以岩针同时削减敌人的体力与精神。并能以魔力将无数的魔像残骸重构起来的巨大魔像。\n\n·对前方四名敌人造成物理伤害（中），并降低其技能值。\n\n·召唤魔像。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301201, /*unit_name*/'水晶魔像', /*prefab_id*/301201, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】以月神魔力的结晶包裹全身的巨大魔像。\n附着于全身的结晶既如坚实的盾牌，又如可穿刺一切的锐矛。\n·当水晶魔像获得毒、猛毒、诅咒、烧伤状态时，\n　行动速度将大幅降低。\n·解除自身所有的减益效果，\n　并大幅提升物理防御力与魔法防御力。\n　此外，中幅提升我方除自身以外同伴的\n　物理防御力与魔法防御力。\n·对敌方全体造成物理伤害（中），使其眩晕。\n·对前方范围内的敌人造成物理伤害（大），\n　并小幅提升我方全体的\n　物理攻击力与魔法攻击力。\n　同时中幅回复自身技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/'暴食魔兽', /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'天秤座魔星兽是恶名昭彰的暴食凶兽。\n衡量夺走的灵魂的善恶，喜欢享受其味道的差异。\n\n·对来自敌人的攻击而造成的麻痹、眩晕、束缚、\n　降低行动速度等效果没有抗性。\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对后方范围内的敌人造成魔法伤害（中）。\n·对前方范围内的敌人造成魔法伤害（中），\n　并小幅降低其技能值。　同时中幅回复\n　自身技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301301, /*unit_name*/'暴食魔兽', /*prefab_id*/301301, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对后方两名角色造成魔法伤害（中）。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。同时中幅提升自身技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301302, /*unit_name*/'暴食魔兽', /*prefab_id*/301302, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】吞噬一切灵魂的天秤座魔星兽。\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\n\n·对敌方全体造成魔法伤害（大），并\n　特大幅度提升自身的物理攻击力与魔法攻击力。\n·对前方三名角色造成魔法伤害（中），\n　并小幅降低其技能值。　同时中幅提升自身技能值。\n·对后方两名角色造成魔法伤害（中）。\n·对前方三名敌人赋予束缚状态，\n　并造成魔法伤害（大）。\n　当自身的目标被击破时，\n　特大幅度提升自身的魔法攻击力。');
