INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306708, /*unit_name*/'丑萌巧克力兽', /*prefab_id*/306700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】因恶毒的言语而暴走的巨大巧克力魔物。\n被误解的呢喃爱意，也会化作伤害他人的刀刃。\n\n·必杀技能时，对敌方全体造成与角色\n　最大生命值相应的物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方三名敌人造成与角色最大生命值\n　相应的物理伤害（中），并使其眩晕。\n　当角色的生命值低于40%时，\n　赋予烧伤状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306709, /*unit_name*/'丑萌巧克力兽', /*prefab_id*/306700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】因恶毒的言语而暴走的巨大巧克力魔物。\n被误解的呢喃爱意，也会化作伤害他人的刀刃。\n\n·必杀技能时，对敌方全体造成与角色\n　最大生命值相应的物理伤害（大），\n　当目标处于烧伤状态时，追加伤害。\n·对前方三名敌人造成与角色最大生命值\n　相应的物理伤害（中），并使其眩晕。\n　当角色的生命值低于40%时，\n　赋予烧伤状态。\n·当丑萌巧克力兽自身的生命值\n　低于45％时，会变得更加凶暴，\n　攻击更为猛烈。');
