INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307007, /*unit_name*/'魔石巨人', /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【MODE3】无畏的巨人，解放了\n全身的力量。在强大无比的破坏力前\n耍小聪明的攻击无法奏效。\n\n·容易受物理攻击力提升·下降的效果\n　影响。\n·必杀技能给予敌方全体物理伤害（大）。\n·给予前方三名角色物理伤害（中）。\n·一定时间内做出防御后，根据期间所受到的\n　伤害量给予敌方全体物理伤害，\n　使前方两名角色麻痹。\n　并且提升自身的物理攻击力。\n·给予敌方全体物理伤害，造成\n　大力的击退效果。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307100, /*unit_name*/'机械莉玛（MODE1）', /*prefab_id*/307100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'拥有坚固多彩的技能，谜之巨大兵器的第1形态。\n使用苹果型的补给机器充填未知的能量，\n注意其使宽广范围焦土化的连续攻击！\n\n·必杀技能对敌方全体造成物理伤害（大）。\n·对前方一名角色造成物理伤害（小）。\n·降低一定范围内的物理攻击力和回避，\n　展开物理伤害的领域。\n·对前方范围内造成物理伤害（中），\n　提升自身的技能值。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307101, /*unit_name*/'机械莉玛（MODE2）', /*prefab_id*/307101, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/0.1, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'切换到攻击一切模式的谜之巨大兵器的第2形态。\n核动力炉释放的炽热光线，将大地燃烧殆尽。\n对毫无防备的巨大兵器造成会心一击！\n\n·必杀技能对前方小范围\n　造成物理伤害（特大），中范围造成物理伤害（大），\n　对宽广范围内造成物理伤害（中），\n　并将其大幅击飞。\n·对前方一名角色造成物理伤害（小）。\n·随机对一名角色造成物理伤害（小）。\n·在一定范围内，降低技能值的上升和回避，\n　展开魔法伤害的领域。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307102, /*unit_name*/'机械莉玛（MODE3）', /*prefab_id*/307102, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.5, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'自我修复损耗的谜之巨大兵器的第3形态。\n防卫无人机会阻止对充满破绽的本体的攻击。\n使用范围攻击将无人机击退，捉住好机会！\n\n·将敌方全体大幅击飞，\n　召唤3台迷你机械莉玛。\n·迷你机械莉玛会对前方一名角色\n　造成物理伤害（小）。\n·迷你机械莉玛随机对一名角色\n　造成魔法伤害（中）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307200, /*unit_name*/'巴吉里斯克', /*prefab_id*/307200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【魔法】追求露娜魔力飞来的大怪鸟。\n率领雏鸟，挥下石化的咒剑。\n\n·对非石化状态的角色全体造成魔法伤害（大）\n　使其短时间石化。\n　对石化状态的角色全员造成最大生命值\n　80%的伤害。\n·对前方三名角色造成魔法伤害（小）。\n　并且因该技能伤害\n　陷入中毒状态的角色会长时间石化。\n·对前方范围内的角色造成魔法伤害（中）\n　并且使其变为中毒状态。\n　自身的生命值降到50％以下时，会对敌方全体\n　做出反击，召唤出可可。\n·可可会对前方范围内物理攻击力\n　最高的一名角色造成物理伤害（小），\n　并且使其陷入中毒状态。\n　当自身的生命值处于最大生命的35%以下时，\n　中幅提升物理攻击力最高的己方同伴的技能值，\n　自身变为无法战斗状态。');
