INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308604, /*unit_name*/'类龙生物', /*prefab_id*/308600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【阶段1】自苍天降下的咆哮撕裂了天空，伪龙在阳光下展开自傲的双翼，降临于此。\n\n·当类龙生物自身的生命值低于60%时，进入至下一阶段。\n\n·在战斗开始时，获得70层“火焰果”。\n·必杀技能会根据类龙生物保持的“火焰果”层数，对敌方全体造成物理伤害。\n　在那之后，减少60层“火焰果”。\n·对前方三名敌人造成物理伤害，并使其眩晕。\n·在一段时间内大幅降低自身的物理防御力与魔法防御力，在进食动作期间，不断获得“火焰果”，最大60层。\n　在动作期间，当类龙生物受到150000以上的伤害时，会中断技能，并减少30层“火焰果”。\n·召唤4只怪物，并小幅度击退敌方全体。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308605, /*unit_name*/'类龙生物', /*prefab_id*/308600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【阶段2】在伪龙的呼唤下，聚居在森林里的魔物集结于此，并将竭尽全力保护它。\n\n·打倒魔像、树精、毒食花以及鬼灯草虫后，将进入下一阶段。\n\n·类龙生物会对眼前一名敌人进行物理伤害。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/308606, /*unit_name*/'类龙生物', /*prefab_id*/308606, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【阶段3】对龙的憧憬终于使蜥蜴觉醒了。\n空中霸者的吐息将会为岛上带来最凶猛的劫火。\n\n·在战斗开始时，获得70层“火焰果”。\n·必杀技能会根据类龙生物保持的“火焰果”层数，对敌方全体造成物理伤害。\n　在那之后，减少60层“火焰果”。\n·对前方三名敌人造成物理伤害，并使其眩晕。\n·在一段时间内大幅降低自身的物理防御力与魔法防御力，在进食动作期间，不断获得“火焰果”，最大60层。\n　在动作期间，当类龙生物受到250000以上的伤害时，会中断技能，并减少30层“火焰果”。\n·当类龙生物自身的生命值低于40%时，攻击会变得更为凶暴。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309000, /*unit_name*/'陆生树懒', /*prefab_id*/309000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】游离在半梦半醒的夹缝间的贪睡树懒。\n被打扰清梦时会大发雷霆，爆发出可怕的力量。\n\n·对敌方全体造成魔法伤害（中），\n　并特大幅度降低其魔法攻击力。\n·对敌方全体造成魔法伤害（小）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309100, /*unit_name*/'鳄龙', /*prefab_id*/309100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】栖息于大河中的巨鳄魔物。\n看似呆笨，却会用那柄奇异的长枪溶解大地，封锁冒险者的行动。\n\n·对敌方全体造成物理伤害（大），并降低其物理防御力与行动速度。\n\n·以前方第三名敌人为中心，造成小范围的物理伤害（小），并赋予束缚状态。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309200, /*unit_name*/'天空女武神', /*prefab_id*/309200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】飞舞于战场上空的女武神。\n会使出让人应接不暇的连射，阻止敌人的前进。\n\n·对前方范围内的四名敌人造成特大伤害，\n　并特大幅度降低其技能值与行动速度。\n·对前方三名敌人造成中等伤害，\n　并提升自身的行动速度。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/309201, /*unit_name*/'天空女武神', /*prefab_id*/309201, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】飞舞于战场上空的女武神。\n会使出让人应接不暇的连射，阻止敌人的前进。\n\n·对前方范围内的四名敌人造成特大伤害，\n　并特大幅度降低其技能值与行动速度。\n·对前方三名敌人造成中等伤害，\n　并提升自身的行动速度。');
