INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310304, /*unit_name*/'反物质兽', /*prefab_id*/310304, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/4.2, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式1】被黑暗的力量所污染，\n以爱与欲望构成的负面魔法生物。将无情地\n扭曲整个世界。\n\n·战斗开始时，大幅提升自身的\n　回避率。\n·必杀技能是从异次元取出道具\n　以道具来发动攻击。根据道具的不同\n　效果也将有所变化。\n　若使用锤子进行攻击，对敌方全体造成物理\n　百分比伤害，并大幅降低其物理攻击力。\n　若使用铜钹进行攻击，对敌方全体造成物理\n　百分比伤害，并大幅降低其魔法攻击力。\n　若使用炸弹进行攻击，对敌方全体造成物理\n　百分比伤害，并赋予诅咒状态。\n·以最前方的敌人为中心，对范围内所有敌人\n　造成物理伤害（中），同时\n　击退敌人。\n·对范围内的所有敌人造成\n　物理伤害（中）。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310305, /*unit_name*/'反物质兽', /*prefab_id*/310300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式2】召唤魔法少女的暗影。\n扭曲而负面的绝望阴影，将考验魔法少女们\n爱与希望的力量。\n\n·默默地守望暗影的战斗。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310306, /*unit_name*/'反物质兽', /*prefab_id*/310300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【模式3】愤怒至极的负面魔法生物\n终于解放了真正的力量。势要彻底摧毁魔法少女的心，\n以扭曲的爱之力令一切屈服。\n\n·必杀技能是从异次元取出道具\n　以道具来发动攻击。根据道具的不同\n　效果也将有所变化。\n　若使用锤子进行攻击，对敌方全体造成物理\n　百分比伤害，并大幅降低其物理攻击力。\n　若使用铜钹进行攻击，对敌方全体造成物理\n　百分比伤害，并大幅降低其魔法攻击力。\n　若使用炸弹进行攻击，对敌方全体造成物理\n　百分比伤害，并赋予诅咒状态。\n·以最前方的敌人为中心，对范围内所有敌人\n　造成物理伤害（中），同时\n　击退敌人。\n·对范围内的所有敌人造成\n　物理伤害（大）。\n·当反物质兽的生命值低于50％时，\n　会展开更为激烈凶暴的攻击。');
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/310400, /*unit_name*/'变色龙', /*prefab_id*/310400, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*cutin_star6*/0, /*visual_change_flag*/0, /*comment*/'【物理】潜藏于沼泽林，暗暗瞄准猎物的\n变色龙魔物。性格胆小而狡猾，\n但被惹怒时也会发动冲动的突进。\n\n·必杀技能会对敌方全体造成\n　物理伤害（大），并大幅降低其\n　物理防御力。\n·对随机一名敌人造成\n　物理伤害（中）。\n·变色龙在经过一段时间后，会通过拟态\n　融入背景，极大幅度提升回避率，\n　特大幅度提升行动速度，大幅\n　降低物理防御力。\n·对前方三名敌人造成\n　物理伤害（中）。');
